let deadline=new Date("31 Dec, 2022 23:59:59").getTime(),counter=setInterval(()=>{let e=(new Date).getTime(),o=deadline-e;days=Math.floor(o/864e5),hours=Math.floor(o%864e5/36e5),minutes=Math.floor(o%36e5/6e4),seconds=Math.floor(o%6e4/1e3),document.querySelector(".count .box .days").innerHTML=days<10?`0${days}`:days,document.querySelector(".count .box .hrs").innerHTML=hours<10?`0${hours}`:hours,document.querySelector(".count .box .mins").innerHTML=minutes<10?`0${minutes}`:minutes,document.querySelector(".count .box .secs").innerHTML=seconds<10?`0${seconds}`:seconds,o<0&&clearInterval(counter)},1e3);