let navbar=document.querySelector("nav .container .links"),navOpen=document.querySelector("nav .container > i"),navClose=document.querySelector("nav .container .links > i"),links=document.querySelectorAll("nav .container .links li");navOpen.addEventListener("click",function(){navbar.classList.toggle("active")}),navClose.addEventListener("click",function(){navbar.classList.remove("active")}),links.forEach(e=>{e.addEventListener("click",e=>{links.forEach(e=>{e.classList.remove("active")}),e.target.parentElement.classList.add("active")})});